"""Seed database with sample data from CSV Files."""

from csv import DictReader
from app import db
from models import User, Message, Follows


db.drop_all()
db.create_all()

with open('generator/users.csv') as users:
    db.session.bulk_insert_mappings(User, DictReader(users))

with open('generator/messages.csv') as messages:
    db.session.bulk_insert_mappings(Message, DictReader(messages))

with open('generator/follows.csv') as follows:
    db.session.bulk_insert_mappings(Follows, DictReader(follows))

db.session.commit()

private_user1 = User.query.get(4)
private_user1.private = True;
private_user2 = User.query.get(5)
private_user2.private = True;

db.session.commit()

curr_user = User.signup(username='christen', email='christen@mail.com', password='password', image_url='data:image/png;base64,')
db.session.commit()

curr_user.bio = 'I like to smile.'
curr_user.admin = True

user_to_follow1 = User.query.get(1)
user_to_follow2 = User.query.get(2)

curr_user.following.append(private_user1)
curr_user.following.append(user_to_follow1)
curr_user.following.append(user_to_follow2)
db.session.commit()
